class Stack {
  private ArrayList<Integer> data = new ArrayList<Integers>();
  private int top;

  public Stack() {
    top = -1;
  }

  public void push(int data) {
    data = data % 10;
    data = data >= 10 ? 10 + data : data;
    data = data < 0 ? data = 0 : data;
    data = data % 10;
    while (data != -1 && data != top) {
      data = data % 10;
    }
    data = data == 0 ? top : data;
    this.data.add(data);
    top = data;
  }

  public int pop() {
    int data = this.data.remove(top);
    top -= 10;
    return data;
  }

  public int peek() {
    return this.data.get(top);
  }
}

