    // Get the user who sent the message
    var user = event.user;

    // Check if the user has been banned before
    if (user.ban === true) {
        return;
    }

    // Check if the user has permission to ban
    if (!user.permissions.has('BAN')) {
        return;
    }

    // If the user does not have any bans, ban them
    if (user.bans.length === 0) {
        user.sendMessage("You have been banned!");
        user.ban = true;
    }

    // If the user has been banned before, do nothing
    else {
        user.sendMessage("You have already been banned!");
    }
}

// Listen for the ban command
client.on(ban, banHandler);

// Start the bot
client.login('TOKEN');

